             </div>
                <div className="scheduleSal">Technician will go in here
            </div>

      <DatePicker
        showTimeSelect
        showTimeSelectOnly
        timeIntervals={15}
        timeCaption="Time"
        dateFormat="h:mm aa"
      />
      </div>
    );
};

export default BookingPage;
