import React, {useEffect, useState} from "react";
import "../style.css";
import axios from "axios";


//MONIQUE, read me:   How are you going to have the options here linked to the technician options? Are you going to 
//need to use PROPS or something like that to execute so the technician list changes depending on the input of the 
//selected salon. Ask Carl. 



function SalonSelect() {

    const [booking, setSalon] = useState({salon: ""});

    function handleSalon() {
        console.log(booking);
        axios.post("/api/booking", booking)
    }

    return (
        <div className="homeDiv booking">
            <h1 className="headingThree">Book Appointment</h1>
            <form className="font" onSelect={handleSalon}>
                <label for="salon">Salon</label>
                <select id="salon" name="salon" className="salInput" value={booking.salon} onChange={(e)=>{
                    const updateBooking = {...booking, salon: e.target.value}
                }}>
                    <option value="">Select</option>
                    <option value="Glo Nails Drummoyne">Glo Nails Drummoyne</option>
                    <option value="Ben's Nails Five Dock">Ben's Nails Five Dock</option>
                </select>
            </form>
        </div>
    );
}

export default SalonSelect;
