import React, {useState} from "react";
import "./style.css";
import axios from "axios";

function Signup() {
    const [user, setUser] = useState({email: "", password: ""});

    function handleSignup(e) { 
        e.preventDefault();
        axios.post("/api")
        }



    return (
        <div className="signupDiv">
            <h2>Sign Up</h2>
            <form>
                <input type="email" value={user.email} onChange={(e)=> {
                    const updatedUser = {...user, email: e.target.value}
                    setUser(updatedUser);
                }} placeholder ="Email"></input>
                <input type="password" value={user.password} onChange={(e)=> {
                    const updatedUser = {...user, password: e.target.value}
                    setUser(updatedUser);
                }} placeholder="Create Password"></input>
                <button onClick={handleSignup} type="submit">Submit</button>
            </form>

        </div>
    );
}


export default Signup;
